#include <iostream> using namespace std; int main() {     int A[100000],B[100000];     int n,m; //n=Size of array A, m=size of array B     cout<<"Enter Size of Array A=";     cin>>n;     cout<<"Enter elements of A=";     for(int i=0;i<n;i++){ //Input array A         int x;         cin>>x;         A[i]=x;     }          cout<<"Enter Size of Array B=";     cin>>m;     cout<<"Enter elements of B=";     for(int i=0;i<m;i++){// input Array B         int x;         cin>>x;         B[i]=x;     }          int **C=new int*[n];    //declare a 2d array C of size n*m     for(int i=0;i<n;i++){         C[i]=new int[m];     }     int ans=0;      //Initialize xor of all element of C =0 at start     for(int i=0;i<n;i++){         for(int j=0;j<m;j++){             C[i][j]=A[i]|B[j]; //Computing Array C[i][j]=A[i]|B[j]             ans=(ans^C[i][j]); //Caculcating xor of all elements         }     }     cout<<"A=";     //print array A     for(int i=0;i<n;i++){         cout<<A[i]<<" ";     }     cout<<endl;     cout<<"B=";     //print array B     for(int i=0;i<m;i++){         cout<<B[i]<<" ";     }     cout<<endl;     cout<<"C="<<endl;     //print array C     for(int i=0;i<n;i++){         for(int j=0;j<m;j++){             cout<<C[i][j]<<" ";         }         cout<<endl;     }     cout<<"Xor of All elements of C array="<<ans;     return 0; }
